DROP PROCEDURE IF EXISTS `Consultas`;

DELIMITER $$

/*!50003 SET @TEMP_SQL_MODE=@@SQL_MODE, SQL_MODE='STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION' */ $$
CREATE DEFINER=`root`@`%` PROCEDURE `Consultas`(Nombre Varchar(60))
BEGIN
IF Nombre='Canje'
THEN
SELECT  
a.Cliente,
a.Categoria,
r.*
From
(
SELECT Fecha,
Recibo,
IdAdjudicacion,
sum(Capital+InteresCte+InteresMora)As 
Valor, 
Estado 
From recaudos
Where Estado='Pendiente'
GROUP by Recibo
)r

join 
(
SELECT 
a.IdAdjudicacion,
a.Contrato,
a.IdTercero1,
t.NombreCompleto as Cliente,
a.Valor,
a.FormaPago,
a.Estado,
c.Nombre as Categoria,
c.Años
from adjudicacion a
left join categorias c on c.IdCategoria=a.IdCategoria
left join 007guardian.terceros t on t.idtercero=a.Idtercero1
)a on a.IdAdjudicacion =r.IdAdjudicacion;
END IF;


IF Nombre='Comision'
THEN
 SELECT  * FROM
(
SELECT
d.Cliente,
d.Valor,
d.Rcd Recaudo,
c.*,
cast((c.Comision1*d.BaseComision*d.Lqd1/100)as dec(10,2) )LqdCom1,
cast((c.Comision2*d.BaseComision*d.Lqd2/100)as dec(10,2) )LqdCom2,
